import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.Response;
import java.io.IOException;

public class NetworkUtils {

    private final OkHttpClient client = new OkHttpClient();

    public String sendGetRequest(String url) throws IOException {
        // Створення запиту
        Request request = new Request.Builder()
                .url(url)
                .build();

        // Виконання запиту
        try (Response response = client.newCall(request).execute()) {
            if (!response.isSuccessful()) {
                throw new IOException("Unexpected code " + response);
            }
            return response.body().string();
        }
    }
}

import okhttp3.*;

import java.io.IOException;

public class NetworkUtils {

    private final OkHttpClient client = new OkHttpClient();

    public String sendPostRequest(String url, String json) throws IOException {
        // Створення тіла запиту
        RequestBody body = RequestBody.create(json, MediaType.get("application/json; charset=utf-8"));

        // Створення запиту
        Request request = new Request.Builder()
                .url(url)
                .post(body)
                .build();

        // Виконання запиту
        try (Response response = client.newCall(request).execute()) {
            if (!response.isSuccessful()) {
                throw new IOException("Unexpected code " + response);
            }
            return response.body().string();
        }
    }
}

public void sendAsyncRequest(String url) {
    Request request = new Request.Builder()
            .url(url)
            .build();

    client.newCall(request).enqueue(new Callback() {
        @Override
        public void onFailure(Call call, IOException e) {
            e.printStackTrace();
        }

        @Override
        public void onResponse(Call call, Response response) throws IOException {
            if (response.isSuccessful()) {
                System.out.println("Response: " + response.body().string());
            }
        }
    });
}

import okhttp3.*;

import java.io.IOException;

public class CustomClient {

    public OkHttpClient getClientWithInterceptor() {
        return new OkHttpClient.Builder()
                .addInterceptor(chain -> {
                    Request request = chain.request().newBuilder()
                            .addHeader("Authorization", "Bearer YOUR_TOKEN")
                            .build();
                    return chain.proceed(request);
                })
                .build();
    }
}

import okhttp3.Cache;
import okhttp3.OkHttpClient;

import java.io.File;

public class CacheConfig {

    public OkHttpClient getClientWithCache(File cacheDir) {
        int cacheSize = 10 * 1024 * 1024; // 10 MB
        Cache cache = new Cache(cacheDir, cacheSize);

        return new OkHttpClient.Builder()
                .cache(cache)
                .build();
    }
}

import okhttp3.*;

public class WebSocketExample {

    private final OkHttpClient client = new OkHttpClient();

    public void connectWebSocket(String url) {
        Request request = new Request.Builder()
                .url(url)
                .build();

        WebSocket webSocket = client.newWebSocket(request, new WebSocketListener() {
            @Override
            public void onOpen(WebSocket webSocket, Response response) {
                System.out.println("WebSocket Opened");
                webSocket.send("Hello, Server!");
            }

            @Override
            public void onMessage(WebSocket webSocket, String text) {
                System.out.println("Message: " + text);
            }

            @Override
            public void onClosed(WebSocket webSocket, int code, String reason) {
                System.out.println("WebSocket Closed: " + reason);
            }

            @Override
            public void onFailure(WebSocket webSocket, Throwable t, Response response) {
                t.printStackTrace();
            }
        });
    }
}
