public class HobbiesConverter {

   @TypeConverter
   public String fromHobbies(List<String> hobbies) {
       return hobbies.stream().collect(Collectors.joining(","));
   }

   @TypeConverter
   public List<String> toHobbies(String data) {
       return Arrays.asList(data.split(","));
   }

}

